// src/Catalog.js
import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe('pk_live_ca8gmEy7wmAAlOxKFqa9Opll00TnfFx9rq');

const Catalog = () => {
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');

  const handleCheckout = async (priceId) => {
    const stripe = await stripePromise;
    const response = await fetch('/api/create-checkout-session', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        priceId,
        email,
        address,
        phone,
      }),
    });

    const session = await response.json();

    if (session.id) {
      stripe.redirectToCheckout({ sessionId: session.id });
    } else {
      console.error('Session ID not returned');
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const priceId = 'price_1PRGj6GzKuYtEoOEUbujzkNM'; // Example price ID
    handleCheckout(priceId);
  };

  return (
    <div>
      <header>
        <h1>Solar Panels Catalog</h1>
      </header>
      <main>
        <section className="product">
          <h2>Solar Panel Model A</h2>
          <p>Description of Solar Panel Model A</p>
          <p>$199.99</p>
          <button
            onClick={() => handleCheckout('price_1PRGj6GzKuYtEoOEUbujzkNM')}
            className="checkout-button"
          >
            Buy Now
          </button>
        </section>
        {/* Add more products as needed */}
      </main>
      <footer>
        <form id="payment-form" onSubmit={handleSubmit}>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            name="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label htmlFor="address">Address:</label>
          <textarea
            name="address"
            id="address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
          <label htmlFor="phone">Phone:</label>
          <input
            type="tel"
            name="phone"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
          <button type="submit">Submit</button>
        </form>
      </footer>
    </div>
  );
};

export default Catalog;
